  #[allow(clippy::clone_on_copy)]
  #[allow(clippy::vec_init_then_push)]
  pub fn invoke_signed(&self, signers_seeds: &[&[&[u8]]]) -> solana_program::entrypoint::ProgramResult {
          let args = BorrowObligationLiquidityInstructionArgs {
                                                              liquidity_amount: self.instruction.liquidity_amount.clone().expect("liquidity_amount is not set"),
                                    };
        let instruction = BorrowObligationLiquidityCpi {
        __program: self.instruction.__program,
                  
          owner: self.instruction.owner.expect("owner is not set"),
                  
          obligation: self.instruction.obligation.expect("obligation is not set"),
                  
          lending_market: self.instruction.lending_market.expect("lending_market is not set"),
                  
          lending_market_authority: self.instruction.lending_market_authority.expect("lending_market_authority is not set"),
                  
          borrow_reserve: self.instruction.borrow_reserve.expect("borrow_reserve is not set"),
                  
          borrow_reserve_liquidity_mint: self.instruction.borrow_reserve_liquidity_mint.expect("borrow_reserve_liquidity_mint is not set"),
                  
          reserve_source_liquidity: self.instruction.reserve_source_liquidity.expect("reserve_source_liquidity is not set"),
                  
          borrow_reserve_liquidity_fee_receiver: self.instruction.borrow_reserve_liquidity_fee_receiver.expect("borrow_reserve_liquidity_fee_receiver is not set"),
                  
          user_destination_liquidity: self.instruction.user_destination_liquidity.expect("user_destination_liquidity is not set"),
                  
          referrer_token_state: self.instruction.referrer_token_state,
                  
          token_program: self.instruction.token_program.expect("token_program is not set"),
                  
          instruction_sysvar_account: self.instruction.instruction_sysvar_account.expect("instruction_sysvar_account is not set"),
                          __args: args,
            };
    instruction.invoke_signed_with_remaining_accounts(signers_seeds, &self.instruction.__remaining_accounts)
  }
